content:
@font-face {
  font-family: "Meiryo";
  src: url("/fonts/Meiryo.eot");
  /* IE9 */
  src: url("/fonts/Meiryo.eot?#iefix") format("embedded-opentype"), /* IE6-IE8 */
  url("/fonts/Meiryo.woff") format("woff"), /* chrome, firefox */
  url("/fonts/Meiryo.ttf") format("truetype"), /* chrome, firefox, opera, Safari, Android, iOS 4.2+ */
  url("/fonts/Meiryo.svg#Meiryo") format("svg");
  /* iOS 4.1- */
  font-style: normal;
  font-weight: normal;
}

.article-content p {
  letter-spacing: 1px;
  font-size: 16px;
  color: #444
}

.content.content-post .article-header .article-meta {
  font-size: 13px
}

.highlight pre {
  font-family: Menlo,"Microsoft YaHei Mono",Consolas,"Courier New",monospace,sans-serif;
}
